import {React, useState} from 'react'
import { Navbar, Nav, Container, Button} from 'react-bootstrap';
import logo from './logo.svg'
import './index.css'

export default function NavBar() {

  const [expand, setExpand] = useState(false);

  return (
    <Navbar className='navbar' bg='dark' expand="lg" sticky='top' expanded={expand}>
      <Container className='nav-container'>
        <Navbar.Brand>
          <img src={logo} alt='logo' className='nav-logo'/>&nbsp;
          Carol's
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbar-collapse" onClick={()=>{setExpand(!expand)}}/>
        <Navbar.Collapse id="navbar-collapse">
          <Nav className="me-auto" onClick={()=>{setExpand(!expand)}}>
            <Nav.Link href='#home'>Introduction</Nav.Link>    {/** #id css选择器格式 */}
            <Nav.Link href='3skills'>Skills</Nav.Link>
            <Nav.Link href='.projects'>Projects</Nav.Link>
          </Nav>

        <Button className='navbar-connect-btn'>Let's connect</Button>

        </Navbar.Collapse>
      </Container>
    </Navbar>
  )
}
