import {React, useState, useEffect} from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import { ArrowRightCircle } from 'react-bootstrap-icons';
import headphoto from './headphoto.svg'
import './index.css'

export default function Introduction() {

  const [idx, setIdx] = useState(0);
  const showInfo = 'Thank you for visiting.';
  const [text, setText] = useState('');

  useEffect(()=>{
    const timer = setInterval(()=>{display()}, 300);
    return ()=>{clearInterval(timer)}
  }, [idx])

  const display = ()=>{
    const len = showInfo.length;
    // 表示已显示完,停留2s，重新写入过程
    if (idx === len){
      setIdx(0);
    }
    // 往前执行
    else{
      setIdx(idx+1);
      setText(showInfo.substring(0,idx+1))
    }
  }

  return (
    <section className='banner' id='home'>
      <Container>
        <Row className='align-row'>
          <Col xs={5} xl={6} lg={6} md={6}>
            <section className='greeting'>
              <h1>Hi! {text}</h1>
              <br/>
              <h2>I am Yujie (Carol) Huang.</h2>
              <br />
              <p>I graduated in September this year, majoring in Msc Computer Science in University of Southampton.
                I have passion for web development. I'm currently learning about React, and seeking for a position as Junior Web Developer.
                I've also learned a bit about web back-end. Even though I'm more familiar with web front-end, it is my goal to be a full stack developer in 5 years.
              </p>
              <p>
              If you are hiring and like my projects, feel free to contact me.
              </p>
              <span><span>Let's connect&nbsp;&n</span><ArrowRightCircle /></span>
            </section>
          </Col>
          <Col xs={5} xl={4} lg={4} md={4} className='introduction-photo-col'>
            <img src={headphoto} alt='headphoto' id='headphoto'></img>
          </Col>
        </Row>
      </Container>
    </section>

  )
}
